mapper.update:
update likecheck
set likecount  = likecount  + 1 
where userid=#{userid} and boardnum=#{boardnum}

update likecheck
set likecount  = 0
where userid=#{userid} and boardnum=#{boardnum}

mapper.select:
SELECT b.boardnum, b.subject, b.price, b.filename
FROM board b, likecheck l
WHERE b.boardnum = l.boardnum and l.userid = #{userid}
