mapper.select:
		SELECT	*
		FROM	(SELECT *
        		FROM webnote
		        WHERE receiver = #{userName}
		        ORDER BY regdate DESC) sub
		WHERE	ROWNUM BETWEEN #{startRow} AND #{endRow}

		SELECT	*
		FROM	(SELECT *
        		FROM webnote
		        WHERE writer = #{userName}
		        ORDER BY regdate DESC) sub
		WHERE	ROWNUM BETWEEN #{startRow} AND #{endRow}
